import React, { useState, useEffect, myRef } from "react";

import "./App.css";
import Home from "./components/home/home";
import Pirsomot from "./components/pirsomot/pirsomot";
import Skarim from "./components/skarim/skarim";

function App() {
  const [loadhome, setLoadhome] = useState(false);
  const [loadskr, setLoadskr] = useState(false);
  const [loadprs, setLoadprs] = useState(false);
  const [loadatr, setLoadatr] = useState(false);
  // const [open, setOpen] = useState(window.pageYOffset);
  const topFunction = () => {
    // document.body.scrollTop = 0;
    // myRef.current.scrollIntoView();
    document.documentElement.scrollTop = 1000;
  };

  return (
    <div className="App" dir="rtl">
      <div className="bar">
        <button
          onClick={() => {
            topFunction();

            setLoadhome(true);
            setLoadskr(false);
            setLoadprs(false);
            setLoadatr(false);
          }}
        >
          דף הבית
        </button>
        <button
          onClick={() => {
            topFunction();
            setLoadskr(true);
            setLoadhome(false);
            setLoadprs(false);
            setLoadatr(false);
          }}
        >
          !!!סקרים
        </button>
        <button
          onClick={() => {
            topFunction();

            setLoadprs(true);
            setLoadhome(false);
            setLoadskr(false);
            setLoadatr(false);
          }}
        >
          פרסומות
        </button>
        <button
          onClick={() => {
            topFunction();

            setLoadatr(true);
            setLoadhome(false);
            setLoadskr(false);
            setLoadprs(false);
          }}
        >
          אתר
        </button>
      </div>
      {loadhome && (
        <div>
          {" "}
          <Home />{" "}
        </div>
      )}
      {loadskr && (
        <div>
          {" "}
          <Skarim />{" "}
        </div>
      )}
      {loadprs && (
        <div>
          {" "}
          <Pirsomot />{" "}
        </div>
      )}
      {loadatr && (
        <div>
          {" "}
          <Home />{" "}
        </div>
      )}
    </div>
  );
}

export default App;
