import React, { useState, useEffect } from "react";

import "./App.css";
import Home from "./components/home/home";
import Pirsomot from "./components/pirsomot/pirsomot";
// import Home from "./components/home/home";

function App() {
  const [loadhome, setLoadhome] = useState(false);
  const [loadskr, setLoadskr] = useState(false);
  const [loadprs, setLoadprs] = useState(false);
  const [loadatr, setLoadatr] = useState(false);
  const [open, setOpen] = useState("open");

  return (
    <div className="App" dir="rtl">
      <header className="App-header">
        <button
          onClick={() => {
            setLoadhome(true),
              setLoadskr(false),
              setLoadprs(false),
              setLoadatr(false);
          }}
        >
          דף הבית
        </button>
        <button
          onClick={
            (() => setLoadskr(true),
            setLoadhome(false),
            setLoadprs(false),
            setLoadatr(false))
          }
        >
          סקרים
        </button>
        <button
          onClick={
            (() => setLoadprs(true),
            setLoadhome(false),
            setLoadskr(false),
            setLoadatr(false))
          }
        >
          פרסומות
        </button>
        <button
          onClick={
            (() => setLoadatr(true),
            setLoadhome(false),
            setLoadskr(false),
            setLoadprs(false))
          }
        >
          אתר
        </button>
      </header>
      {loadhome && (
        <div>
          {" "}
          <Home />{" "}
        </div>
      )}
      {loadskr && (
        <div>
          {" "}
          <Pirsomot />{" "}
        </div>
      )}
      {loadprs && (
        <div>
          {" "}
          <Pirsomot />{" "}
        </div>
      )}
      {loadatr && (
        <div>
          {" "}
          <Home />{" "}
        </div>
      )}
    </div>
  );
}

export default App;
