import "./kesher.css";
import axios from "axios";

function Kesher() {
  let product = {};
  // console.log(product);
  const addProduct = () => {
    axios
      .post("http://localhost:4200/api/sendemail/", product)
      .then((res) => console.log(res.data));
  };

  return (
    <div className="kesher">
      רוצים להיות ראשונים להתעדכן על הטבות? אפשרויות חדשות לעשות כסף? הכניסו את
      המייל שלכם ואנו נעדכן אתכם ראשונים.
      {/* <input className="inputkesher" placeholder="הכנס כתובת מייל" />
      <button className="btnkesher">שלח</button> */}
      <form
        action="mailto:yosef9987@gmail.com"
        method="GET"
        type="text/plain"
        target="_top"
      >
        <input type="text" name="name" />
        <input type="text" name="Email" />

        <input type="submit" name="submit" value="Send" />
      </form>
      <input
        id="name"
        type="text"
        onChange={(e) => (product.name = e.target.value)}
        placeholder="name"
      />
      <input
        id="mail"
        type="text"
        onChange={(e) => (product.email = e.target.value)}
        placeholder="mail"
      />{" "}
      <button
        className="btn"
        onClick={() => {
          addProduct();
          console.log(product);
        }}
      >
        Add
      </button>
    </div>
  );
}

export default Kesher;
