import React, { useState, useEffect, myRef } from "react";

import "./App.css";
import Home from "./components/home/home";
import Header from "./header.jpg";
import Pirsomot from "./components/pirsomot/pirsomot";
import Skarim from "./components/skarim/skarim";
import Kesher from "./components/kesher/kesher";
import List from "./components/list/list";

function App() {
  const [loadhome, setLoadhome] = useState(true);
  const [loadskr, setLoadskr] = useState(false);
  const [loadprs, setLoadprs] = useState(false);
  const [loadatr, setLoadatr] = useState(false);
  const [open, setOpen] = useState(false);

  const topFunction = () => {
    document.documentElement.scrollTop = 0;
  };

  const divhome = () => {
    topFunction();
    setOpen(true);
    setLoadhome(true);
    setLoadskr(false);
    setLoadprs(false);
    setLoadatr(false);
  };

  return (
    <div className="App" dir="rtl">
      <div className="bar">
        <button className={loadhome ? "divtrue" : "divfalse"} onClick={divhome}>
          דף הבית
        </button>
        <button
          className={loadskr ? "divtrue" : "divfalse"}
          onClick={() => {
            topFunction();
            setLoadskr(true);
            setLoadhome(false);
            setLoadprs(false);
            setLoadatr(false);
          }}
        >
          סקרים
        </button>
        <div className="divurl">
          <p>caspomat.co.il - האתר שיביא לכם כסף</p>
        </div>

        <button
          className={loadprs ? "divtrue" : "divfalse"}
          onClick={() => {
            topFunction();
            setLoadprs(true);
            setLoadhome(false);
            setLoadskr(false);
            setLoadatr(false);
          }}
        >
          פרסומות
        </button>
        <button
          className={loadatr ? "divtrue" : "divfalse"}
          onClick={() => {
            topFunction();

            setLoadatr(true);
            setLoadhome(false);
            setLoadskr(false);
            setLoadprs(false);
          }}
        >
          אתר
        </button>
      </div>
      <img className="imgheader" src={Header} alt="" />
      {/* <div>.</div>
      </div> */}
      <div className="aaa">
        <div>
          <Kesher />
          {/* <List /> */}
        </div>
        {loadhome && (
          <div>
            {" "}
            <Home divhome={divhome} />{" "}
          </div>
        )}
        {loadskr && (
          <div>
            {" "}
            <Skarim />{" "}
          </div>
        )}
        {loadprs && (
          <div>
            {" "}
            <Pirsomot />{" "}
          </div>
        )}
        {loadatr && (
          <div>
            {" "}
            <Home />{" "}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
