import "./kesher.css";
import axios from "axios";
import React from "react";

function Kesher() {
  let product = {};
  // const [value, setValue] = useState("");

  const addProduct = () => {
    axios
      .post("http://localhost:4200/api/sendemail/", product)
      .then((res) => console.log("res.data"));
    // setValue("");

    document.getElementById("name").value = "";
    document.getElementById("mail").value = "";
    document.getElementById("message").value = "";
  };

  return (
    <div className="kesher">
      <form method="GET">
        <h5>
          רוצים להיות ראשונים להתעדכן על הטבות? אפשרויות חדשות לעשות כסף? הכניסו
          את המייל שלכם ואנו נעדכן אתכם ראשונים.
        </h5>
        {/* <form
          action="http://localhost:4200/api/sendemail/"
          method="GET"
          type="text/plain"
          // target="_top"
        >
          <input type="text" name="name" />
          <input type="text" name="Email" />
          <input type="submit" name="submit" value="Send" />
        </form> */}
        <input
          id="name"
          // value={value}
          className="inputKesher"
          type="text"
          onInput={(e) => (product.name = e.target.value)}
          placeholder="שם"
        />
        <input
          id="mail"
          className="inputKesher"
          type="text"
          onChange={(e) => (product.email = e.target.value)}
          placeholder="מייל"
        />{" "}
        <textarea
          id="message"
          className="textareaKesher"
          type="text"
          onChange={(e) => (product.message = e.target.value)}
          placeholder="שתף אותנו..."
        />{" "}
        <button
          className="btnKesher"
          type="reset"
          onClick={() => {
            addProduct();
            // document.getElementById("name").value = "";
            // console.log(product);
          }}
        >
          שלח
        </button>
      </form>
      <form role="form" action="/send-mail" method="POST">
        <div class="form-group">
          <label for="to">To</label>
          <input
            type="text"
            class="form-control"
            id="to"
            name="to"
            placeholder="Receiver Email"
            // required
          />
        </div>
        <div class="form-group">
          <label for="subject">Subject</label>
          <input
            type="text"
            class="form-control"
            id="subject"
            name="subject"
            placeholder="Subject"
            // required
          />
        </div>
        <div class="form-group">
          <label for="message">Message</label>
          <textarea
            class="form-control"
            id="message"
            name="message"
            placeholder="Message"
            // required
          ></textarea>
        </div>
        <div class="text-center">
          <button
            type="submit"
            id="submit"
            name="submit"
            class="btn 
        btn-primary btn-sm"
          >
            Send
          </button>
        </div>
      </form>
    </div>
  );
}

export default Kesher;
