import React, { useState, useEffect, myRef } from "react";

import "./App.css";
import Home from "./components/home/home";
import Header from "./header.jpg";
import Pirsomot from "./components/pirsomot/pirsomot";
import Skarim from "./components/skarim/skarim";
import Kesher from "./components/kesher/kesher";
import List from "./components/list/list";

function App() {
  const [loadhome, setLoadhome] = useState(true);
  const [loadskr, setLoadskr] = useState(false);
  const [loadprs, setLoadprs] = useState(false);
  const [loadatr, setLoadatr] = useState(false);
  const [open, setOpen] = useState(false);

  const topFunction = () => {
    document.documentElement.scrollTop = 0;
  };

  const divhome = () => {
    topFunction();
    setLoadhome(true);
    setLoadskr(false);
    setLoadprs(false);
    setLoadatr(false);
  };

  const divskr = () => {
    topFunction();
    setLoadskr(true);
    setLoadhome(false);
    setLoadprs(false);
    setLoadatr(false);
  };
  const divprs = () => {
    topFunction();
    setLoadprs(true);
    setLoadhome(false);
    setLoadskr(false);
    setLoadatr(false);
  };
  const divatr = () => {
    topFunction();
    setLoadatr(true);
    setLoadhome(false);
    setLoadskr(false);
    setLoadprs(false);
  };

  return (
    <div className="App" dir="rtl">
      <div className="bar">
        <button className={loadhome ? "divtrue" : "divfalse"} onClick={divhome}>
          דף הבית
        </button>
        <button className={loadskr ? "divtrue" : "divfalse"} onClick={divskr}>
          סקרים
        </button>
        <div className="divurl">
          <p>caspomat.co.il - האתר שיביא לכם כסף</p>
        </div>

        <button className={loadprs ? "divtrue" : "divfalse"} onClick={divprs}>
          פרסומות
        </button>
        <button className={loadatr ? "divtrue" : "divfalse"} onClick={divatr}>
          אתר
        </button>
      </div>
      <img className="imgheader" src={Header} alt="" />
      {/* <div>.</div>
      </div> */}
      <div className="aaa">
        <div>
          <Kesher />
          {/* <List /> */}
        </div>
        {loadhome && (
          <div>
            {" "}
            <Home divskr={divskr} divprs={divprs} divatr={divatr} />{" "}
          </div>
        )}
        {loadskr && (
          <div>
            {" "}
            <Skarim />{" "}
          </div>
        )}
        {loadprs && (
          <div>
            {" "}
            <Pirsomot />{" "}
          </div>
        )}
        {loadatr && (
          <div>
            {" "}
            <Home />{" "}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
