import React, { useState } from "react";

import "./App.css";
import Home from "./components/home/home";
import Pirsomot from "./components/pirsomot/pirsomot";
import Skarim from "./components/skarim/skarim";
import Kesher from "./components/kesher/kesher";
// import List from "./components/list/list";
import HeaderHome from "./header.jpg";
import HeaderSkr from "./components/skarim/header.jpg";
import HeaderPrs from "./headerprs.jpg";

function App() {
  const [loadhome, setLoadhome] = useState(true);
  const [loadskr, setLoadskr] = useState(false);
  const [loadprs, setLoadprs] = useState(false);
  const [loadatr, setLoadatr] = useState(false);
  const [loadkshr, setLoadkshr] = useState(false);

  const topFunction = () => {
    document.documentElement.scrollTop = 0;
  };

  const divhome = () => {
    topFunction();
    setLoadhome(true);
    setLoadskr(false);
    setLoadprs(false);
    setLoadatr(false);
    setLoadkshr(false);
  };

  const divskr = () => {
    topFunction();
    setLoadskr(true);
    setLoadhome(false);
    setLoadprs(false);
    setLoadatr(false);
    setLoadkshr(false);
  };
  const divprs = () => {
    topFunction();
    setLoadprs(true);
    setLoadhome(false);
    setLoadskr(false);
    setLoadatr(false);
    setLoadkshr(false);
  };
  const divatr = () => {
    topFunction();
    setLoadatr(true);
    setLoadhome(false);
    setLoadskr(false);
    setLoadprs(false);
    setLoadkshr(false);
  };

  const divkshr = () => {
    topFunction();
    setLoadkshr(true);
    setLoadatr(false);
    setLoadhome(false);
    setLoadskr(false);
    setLoadprs(false);
  };

  return (
    <div className="App" dir="rtl">
      <div className="bar">
        <div class="allURL">
          <button
            className={loadhome ? "divtrue" : "divfalse"}
            onClick={divhome}
          >
            דף הבית
          </button>
          <button className={loadskr ? "divtrue" : "divfalse"} onClick={divskr}>
            סקרים בתשלום
          </button>
          <button className={loadprs ? "divtrue" : "divfalse"} onClick={divprs}>
            שיווק שותפים
          </button>
          {/* <button className={loadatr ? "divtrue" : "divfalse"} onClick={divatr}>
            כסף מפרסום
          </button> */}
          <button
            className={loadkshr ? "divtrue" : "divfalse"}
            onClick={divkshr}
          >
            צור קשר{" "}
          </button>
        </div>
      </div>
      {/* <div className="adsr"></div>
      <div className="adsl"></div>
      <div className="adsbtm"></div> */}
      <img
        className="imgheader"
        src={
          loadhome ? HeaderHome : loadskr ? HeaderSkr : loadprs ? HeaderPrs : ""
        }
        alt=""
      />
      {loadhome && (
        <div>
          {" "}
          <Home divskr={divskr} divprs={divprs} divatr={divatr} />{" "}
        </div>
      )}
      {loadskr && (
        <div>
          {" "}
          <Skarim />{" "}
        </div>
      )}
      {loadprs && (
        <div>
          {" "}
          <Pirsomot divatr={divatr} />{" "}
        </div>
      )}
      {loadatr && (
        <div>
          {" "}
          <Home divskr={divskr} divprs={divprs} divatr={divatr} />{" "}
        </div>
      )}
      {loadkshr && (
        <div>
          {" "}
          <Kesher />
        </div>
      )}
      {/* <List /> */}
    </div>
  );
}

export default App;
