import React, { useState, useEffect, myRef } from "react";

import "./App.css";
import Home from "./components/home/home";
import Pirsomot from "./components/pirsomot/pirsomot";
import Skarim from "./components/skarim/skarim";

function App() {
  const [loadhome, setLoadhome] = useState(false);
  const [loadskr, setLoadskr] = useState(false);
  const [loadprs, setLoadprs] = useState(false);
  const [loadatr, setLoadatr] = useState(false);
  // const [open, setOpen] = useState(window.pageYOffset);
  const topFunction = () => {
    // document.body.scrollTop = 0;
    // myRef.current.scrollIntoView();
    document.documentElement.scrollTop = 0;
  };

  var header = document.getElementById("myDIV");
  var btns = header.getElementsByClassName("btn");
  for (var i = 0; i < btns.length; i++) {
    btns[i].addEventListener("click", function () {
      var current = document.getElementsByClassName("active");
      current[0].className = current[0].className.replace(" active", "");
      this.className += " active";
    });
  }

  return (
    <div className="App" dir="rtl">
      <div className="bar" id="myDIV">
        <button
          className="btn active"
          onClick={() => {
            // topFunction();

            setLoadhome(true);
            setLoadskr(false);
            setLoadprs(false);
            setLoadatr(false);
          }}
        >
          דף הבית
        </button>
        <button
          className="btn"
          onClick={() => {
            // topFunction();
            setLoadskr(true);
            setLoadhome(false);
            setLoadprs(false);
            setLoadatr(false);
          }}
        >
          סקרים
        </button>
        <button
          className="btn"
          onClick={() => {
            // topFunction();
            setLoadprs(true);
            setLoadhome(false);
            setLoadskr(false);
            setLoadatr(false);
          }}
        >
          פרסומות
        </button>
        <button
          className="btn"
          onClick={() => {
            // topFunction();

            setLoadatr(true);
            setLoadhome(false);
            setLoadskr(false);
            setLoadprs(false);
          }}
        >
          אתר
        </button>
      </div>
      <br />
      <br />
      <br />
      <br />
      {loadhome && (
        <div>
          {" "}
          <Home />{" "}
        </div>
      )}
      {loadskr && (
        <div>
          {" "}
          <Skarim />{" "}
        </div>
      )}
      {loadprs && (
        <div>
          {" "}
          <Pirsomot />{" "}
        </div>
      )}
      {loadatr && (
        <div>
          {" "}
          <Home />{" "}
        </div>
      )}
    </div>
  );
}

export default App;
