import React, { useState, useEffect, myRef } from "react";

import "./App.css";
import Home from "./components/home/home";
import Pirsomot from "./components/pirsomot/pirsomot";
import Skarim from "./components/skarim/skarim";

function App() {
  const [loadhome, setLoadhome] = useState(true);
  const [loadskr, setLoadskr] = useState(false);
  const [loadprs, setLoadprs] = useState(false);
  const [loadatr, setLoadatr] = useState(false);
  const [open, setOpen] = useState(false);
  const topFunction = () => {
    // document.body.scrollTop = 0;
    // myRef.current.scrollIntoView();
    document.documentElement.scrollTop = 0;
  };
  return (
    <div className="App" dir="rtl">
      <div className="bar">
        <button
          className={!loadhome ? "aaa" : "gggg"}
          onClick={() => {
            topFunction();
            setOpen(true);
            setLoadhome(true);
            setLoadskr(false);
            setLoadprs(false);
            setLoadatr(false);
          }}
        >
          דף הבית
        </button>
        <button
          className={!loadskr ? "aaa" : "gggg"}
          onClick={() => {
            topFunction();
            setLoadskr(true);
            setLoadhome(false);
            setLoadprs(false);
            setLoadatr(false);
          }}
        >
          סקרים
        </button>
        <button
          className={!loadprs ? "aaa" : "gggg"}
          onClick={() => {
            topFunction();
            setLoadprs(true);
            setLoadhome(false);
            setLoadskr(false);
            setLoadatr(false);
          }}
        >
          פרסומות
        </button>
        <button
          className={!loadatr ? "aaa" : "gggg"}
          onClick={() => {
            topFunction();

            setLoadatr(true);
            setLoadhome(false);
            setLoadskr(false);
            setLoadprs(false);
          }}
        >
          אתר
        </button>
      </div>
      <br />
      <br />
      <br />
      <br />
      {loadhome && (
        <div>
          {" "}
          <Home />{" "}
        </div>
      )}
      {loadskr && (
        <div>
          {" "}
          <Skarim />{" "}
        </div>
      )}
      {loadprs && (
        <div>
          {" "}
          <Pirsomot />{" "}
        </div>
      )}
      {loadatr && (
        <div>
          {" "}
          <Home />{" "}
        </div>
      )}
    </div>
  );
}

export default App;
